/**
 * tab 刷新
 * @param tabsObj
 * @param cfg
 */
function refreshTab(tabsObj,cfg){
    var refresh_tab = cfg.tabTitle?tabsObj.tabs('getTab',cfg.tabTitle):tabsObj.tabs('getSelected');
    if(refresh_tab && refresh_tab.find('iframe').length > 0){
        var _refresh_ifram = refresh_tab.find('iframe')[0];
        var refresh_url = cfg.url?cfg.url:_refresh_ifram.src;
        //_refresh_ifram.src = refresh_url;
        _refresh_ifram.contentWindow.location.href=refresh_url;
    }
}

/*
 * 关闭tab, 用于右键菜单
 */
function closeTab(tabsObj,menu, type){
    var allTabs = tabsObj.tabs("tabs");
    var allTabTitle = [];
    $.each(allTabs, function(i, n){
        var opt = $(n).panel("options");
        if(opt.closable){
            allTabTitle.push(opt.title);
        }
    });
    var curTabTitle = $(menu).data("tabTitle");
    switch(type){
        case 1:
            //关闭当前
            tabsObj.tabs("close", curTabTitle);
            return false;
            break;
        case 2:
            //关闭其他
            for(var i = 0; i < allTabTitle.length; i++){
                if(curTabTitle != allTabTitle[i]){
                    tabsObj.tabs("close",allTabTitle[i]);
                }
            }
            tabsObj.tabs("select",curTabTitle);
            break;
        case 3:
            //关闭所有
            for(var i = 0; i < allTabTitle.length; i++){
                tabsObj.tabs("close",allTabTitle[i]);
            }
            break;
    }
}


;(function($){
	$.insdep={
		ajax:function(c){
			var d={
				url:"",
				async:false,//true异步请求,false同步请求,注：同步请求将锁住浏览器，用户其它操作必须等待请求完成才可以执行
				cache:false,
				type:"POST",
				dataType:"html",
				beforeSend:function(){
					$.messager.progress();
				},
				complete:function(){
					$.messager.progress('close');
				}
			};
			var n=$.extend(true,d,c);
			$.ajax(n);
		},
		error:function(result) {
			$('<div/>').window({
				id:"insdep-error-debug-window",
				cache:false,
			    width : 720,
			    height : 480,
			    modal : true,
				title:"错误",
				content:"<div style='padding:15px;overflow:hidden;height:auto;clear:both;'><b>Return Error</b><br/>"+result+"<br/></div>",
				border:false,
				collapsible:false,
				minimizable:false,
				maximizable:false,
				queryParams:"",
				onClose:function() {
					$(this).window('destroy');
				},
				buttons:[]
			});
		},
		window:function(c){
			var d={
               id:"temp-window",
               href:"",
               cache:false,
               method:"post",
               width : 680,
               height : 550,
               modal : true,
               title:"",
               border:false,
               collapsible:false,
               minimizable:false,
               maximizable:false,
               queryParams:"",
               onClose:function() {
                  $(this).window('destroy');
               },
               buttons:[]
			};
			var n=$.extend(true,d,c);
			$('<div/>').window(n);
		},
		/*替换 panel 方式切换内容 */
		control:function(url,queryParams){
			$('#control').panel({
				href:url,
				cache:false,
				method:"post",
				queryParams:queryParams?queryParams:{},
				onLoad:function(){
					//alert('loaded successfully');
				},
				onBeforeLoad:function(){
					$(this).panel('clear');
				}
			});
		},
		/*自定义 tab 扩展*/
		tabControl:function(url,queryParams,title){
			var tabsControl = $('#tabsControl');
			if(!tabsControl.hasClass("easyui-tabs")){
                tabsControl.tabs({
                    fit:true,
                    border:false,

                    //为其附加鼠标右键事件
                    onContextMenu: function(e, title, index){
                        //alert("right");
                        //该方法通知浏览器不要执行与此事件关联的默认动作
                        //即屏蔽了浏览器在tab页上的鼠标右键事件
                        e.preventDefault();
                        var mm = $("#tabsMenu");
                        //显示右键菜单
                        mm.menu("show",{
                            top: e.pageY,
                            left: e.pageX
                        }).data("tabTitle",title);
                        //为右键菜单选项绑定事件
                        mm.menu({
                            onClick: function(item){
                                closeTab(tabsControl,this, item.name);
                            }
                        });
                    }

                });
			}
            if(tabsControl.tabs("exists",title)){
                tabsControl.tabs("select",title);
                refreshTab(tabsControl,{tabTitle:title,url:url});
            }else{

            	 if (url){
                     var content = '<iframe scrolling="no" frameborder="0"  src="'+url+'" style="width:100%;height:100%;"></iframe>';
                 } else {
                     var content = '未实现';
                 }
                tabsControl.tabs('add',{
                    title:title,
                    content:content,
                    fit:true,
                   /* cache:false,*/
                    closable:true
                });
			}

        }
	};
})(jQuery);
